import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import {Router, Route, IndexRoute, hashHistory,browserHistory} from 'react-router';
import { Provider } from 'react-redux';
import { createStore, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import reducers from './reducers';
import { I18n ,I18nextProvider} from 'react-i18next';
import i18n from './translation';
import { syncHistoryWithStore } from "react-router-redux";
import configureStore from "./store/configureStore";

import * as actions from './actions/index';



//components

import HomePage from './components/HomePage'


const store = configureStore();

// Create an enhanced history that syncs navigation events with the store
const history = syncHistoryWithStore(browserHistory, store);






function Authentication(nextState, replace) {
    const isAuthenticated = true;
     // isAuthenticated = localStorage.getItem("authData") ? true : false;
      if (!isAuthenticated) {
        replace({
          pathname: "/",
          state: { nextPathname: nextState.location.pathname }
        });
      }
}

ReactDOM.render(
    <Provider store={store}>
      <Router history={hashHistory}>
          <Route path="/" >
              <IndexRoute component={LoginPage} />
              <Route path="login" component={App} />

              <Route path="/logout" component={Logout} />
              <Route path="app"  component={Layout} onEnter={Authentication}>
                  <Route path="start" component={HomePage} />
              </Route>
          </Route>
      </Router>
   </Provider>
    , document.getElementById('app'));



// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
