import React, { Component } from 'react';
import PropTypes from 'prop-types';

import * as actions from '../actions/index';




function validate(rut, password) {
  // true means invalid, so our conditions got reversed
  return {
    rut: rut.length === 0,
    password: password.length === 0,
  };
}


class SignUpPage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      rut:"",
      password:"",
      everFocusedRut: false,
      everFocusedPassword: false,
      inFocus: "",
      errors : {},
      blockError : false,
      invalidRut: false,
      errorStatus : false,
      validRut : false,
      wentWrong : false,
      isLoading : false,
      invalidRutPassword : false,
    }

    this.handleRutChange = this.handleRutChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onCheckValidRut = this.onCheckValidRut.bind(this)

  }



  handleRutChange(name,rutt){


    this.setState({ rut : rutt})

  }

  handlePasswordChange(evt){
    this.state.errorStatus = false;
    document.getElementById('wentWrong').style.display = "none";
    this.state.errors.password = "" ;
    this.setState({ password: evt.target.value,isLoading : false,invalidRutPassword : false  });
    var rutError = document.getElementById("rut");
    rutError.classList.remove("invalid-field");
    var passwordError = document.getElementById("password");
    passwordError.classList.remove("invalid-field");
  }

  onCheckValidRut(){
    var checkString = document.getElementById("rut").value;
    if (/[^A-Za-z0-9._-\d]/.test(checkString)) {
      this.setState({errorStatus : true,everFocusedRut:true})
      this.state.errors.rut = "Por favor, digite apenas letra e caracteres numéricos"; //only nos and characters
      document.getElementById("rut").classList.remove("formLogin");
      document.getElementById("rut").classList.add("error");
      return (false);
    }
    if (this.state.validRut == false) {
       this.setState({invalidRut : true})
       document.getElementById("rut").classList.add("error");
    }
    else {
       this.setState({invalidRut : false,errorStatus : false,everFocusedRut:false})
       document.getElementById("rut").classList.remove("error");
    }
  }

  handleSubmit(evt){
    evt.preventDefault();
    const required  = "This field is required!";
    let errors = this.state.errors;
    var checkString = document.getElementById("rut").value;
    if(!this.state.rut){
      var rutError = document.getElementById("rut");
      rutError.classList.add("invalid-field");
      this.state.errorStatus = true;
      errors.rut = "Rut es obligatorio!!" ;


    }
    if(!this.state.password){
       this.state.errorStatus = true;
       errors.password = "La clave es obligatoria!!";
       var passwordError = document.getElementById("password");
       passwordError.classList.add("invalid-field");

    }

    if(this.state.rut){
        // this.context.router.push('/create-password');
        if ( /[^A-Za-z0-9._-\d]/.test(checkString)) {
            this.setState({errorStatus : true,everFocusedRut:true})
            errors.rut = "Por favor, digite apenas letra e caracteres numéricos"; //only nos and characters
            document.getElementById("rut").classList.remove("formLogin");
            document.getElementById("rut").classList.add("error");
            this.state.everFocusedRut = true;
            return (false);
        }

        else {
            if(this.state.password){
              if(this.state.rut.length > 7 && this.state.validRut){
                this.setState({isLoading : !this.state.isLoading})
                let loginData = {
                  username : this.state.rut,
                  password: this.state.password,
                  client_id: "restClient",
                  client_secret: "restSecret",
                  grant_type: "password",
                  scope: "portalConvenios"
                };



                actions.checkLogin(loginData).done((data, status, headers) => {
                    if (data.loginResponse && data.loginResponse.serviceStatus && data.loginResponse.serviceStatus.code == 200) {
                      this.props.actions.saveAuthDataToStore(data);
                      this.setState({isLoading : false})
                      let value = data.loginResponse && data.loginResponse.solicitud && data.loginResponse.solicitud.length && (data.loginResponse.solicitud.length >0 ) ? data.loginResponse.solicitud[0].idSolicitud : "";
                      let roles = data.loginResponse.solicitud && data.loginResponse.solicitud.length && (data.loginResponse.solicitud.length >0 ) && data.loginResponse.solicitud[0] && data.loginResponse.solicitud[0].moduleAccess ? data.loginResponse.solicitud[0].moduleAccess : "";
                      roles = roles.map(function(x){ return x.toLowerCase() })
                      localStorage.setItem('solicitud',value);
                      localStorage.setItem('rut',loginData.username);
                      localStorage.setItem('roles',JSON.stringify(roles));
                      localStorage.setItem('lastlogin',data.loginResponse.lasLogin);
                      localStorage.setItem('access_token',data.access_token);
                      localStorage.setItem('ref-auth',data.refresh_token);
                      if(JSON.parse(localStorage.getItem('roles')).indexOf('admin')!==-1 || JSON.parse(localStorage.getItem('roles')).indexOf('home')!==-1){
                        this.context.router.push('/app/start');
                      }else if(JSON.parse(localStorage.getItem('roles')).indexOf('vobo')!==-1){
                          this.context.router.push('/app/visto-buena');
                        }
                        else if(JSON.parse(localStorage.getItem('roles')).indexOf('beneficios')!==-1){
                          this.context.router.push('/app/benefits-alliances');
                        }
                        else if(JSON.parse(localStorage.getItem('roles')).indexOf('info_finance')!==-1){
                          this.context.router.push('/app/financial-information');
                        }
                        else if(JSON.parse(localStorage.getItem('roles')).indexOf('user_search')!==-1){
                          this.context.router.push('/app/search-partner');
                        }
                        else if(JSON.parse(localStorage.getItem('roles')).indexOf('planillas')!==-1){
                          this.context.router.push('/app/planning');
                        }
                        else if(JSON.parse(localStorage.getItem('roles')).indexOf('user_admin')!==-1){
                          this.context.router.push('/app/profile-administrator');
                        }else{
                          this.context.router.push('/app/default-app');
                        }
                    }
                    if (data && data.loginResponse && data.loginResponse.serviceStatus && data.loginResponse.serviceStatus.code && data.loginResponse.serviceStatus.code == 400) {
                      this.setState({invalidRut : false,wentWrong : false,blockError:true,isLoading : false})
                    }
                    if (data && data.loginResponse && data.loginResponse.serviceStatus && data.loginResponse.serviceStatus.code && data.loginResponse.serviceStatus.code == 300 && data.loginResponse.resetPassword == true) {
                        this.props.actions.saveAuthDataToStore(data);
                        let value = data.loginResponse && data.loginResponse.solicitud && data.loginResponse.solicitud.length && (data.loginResponse.solicitud.length >0 ) ? data.loginResponse.solicitud[0].idSolicitud : "";
                        localStorage.setItem('rut',loginData.username);
                        localStorage.setItem('solicitud',value);
                        localStorage.setItem('lastlogin',data.loginResponse.lasLogin);
                        localStorage.setItem('access_token',data.access_token);
                        localStorage.setItem('ref-auth',data.refresh_token);
                        this.context.router.push('/create-password');
                    }
                    if(data && data.loginResponse && data.loginResponse.serviceStatus && data.loginResponse.serviceStatus.code && data.loginResponse.serviceStatus.code == 500){
                      this.setState({wentWrong : false,blockError:false,invalidRutPassword: true,isLoading : false})
                    }

                    if (data && data.loginResponse && data.loginResponse.serviceStatus && data.loginResponse.serviceStatus.code && data.loginResponse.serviceStatus.code == 800) {
                      this.setState({wentWrong : true,blockError:false,invalidRut:false,isLoading : false})
                    }
                })
              }
              else{
                  this.setState({invalidRut : false,errorStatus:true,isLoading : false})
                  errors.rut = "Rut o contraseña inválida " ;
                  var rutError = document.getElementById("rut");
                  rutError.classList.add("invalid-field");
              }

            }

            else {
              this.state.errorStatus = true;
              errors.rut = "" ;
              errors.password = "La clave es obligatoria!!"
              var passwordError = document.getElementById("password");
              passwordError.classList.add("invalid-field");
            }


        }

    }
    this.setState({errors});
    if(Object.keys(errors).length){
       return '';
    }
  }

